            <p>
              {project.name}
            </p>
            <div>
              <ProjectLanguages logos={project.languages} />
              <img
              style={{
                marginTop: "90px",
                marginLeft: "300px",
                width: "45px",
                height: "35px"
            }}
            src={`/Images/${project.orgimage}`}
            onError={(event) => event.target.style.display = 'none'}
