import React from "react";
import ProjectLanguages from "../projectLanguages/ProjectLanguages";
import "./ProjectCard.css";
import { Fade, Flip } from "react-reveal";
import { right, style } from "glamor";


export default function ProjectCard({ repo: project, theme }) {
  function openRepoinNewTab(url) {
    var win = window.open(url, "_blank");
    win.focus();
  }

  const card = document.getElementById("card")

  card.addEventListener("click",flipCard);

  function flipCard() {
    card.classList.toggle("flipCard");
  }

  const styles = style({
    color: "rgb(88, 96, 105)",
    backgroundColor: "rgb(255, 255, 255)",
    boxShadow: "rgba(0, 0, 0, 0.2) 0px 10px 30px -15px",
    padding: "2rem",
    cursor: "pointer",
    borderRadius: "5px",
    height: "100%",
    transition: "all 0.2s ease-in-out",
    ":hover": {
      boxShadow: `${theme.imageDark} 0 2px 15px`,
    },
  });

  let orgimage = console.log(project.logo_path);

  return (
    <div>
      <Fade bottom duration={2000} distance="40px">
        <div
          className="maincontainer"
          {...styles}
          key={project.name}
          onClick={() => openRepoinNewTab(project.url)}
          style={{ backgroundColor: '#003060', color: 'white' }}
        >
          <div className="card" id="card">
          <div className="front repo-name-div">
          <div>
            <p className="repo-name" style={{ color: 'white' }}>
              {project.name}
            </p>
          </div>
          <div className="repo-details">
            <ProjectLanguages logos={project.languages} />
            {/* <img
            style={{
              Width: "50px",
              Height: "37px",
              // transform: "scale(50%, 50%)",
            }}
            src={`../../assests/images/${project.logo_path}`}
            alt={project.orgname}
          /> */}
          </div>
        </div>
        <div className="back repo-description">
        <p style={{ color: 'white' }}>
            {project.description}
          </p>
        </div>
        </div>
        </div>
      </Fade>
    </div>
  );
}
